import React, { useState } from 'react';

const TaskForm = ({ onAddTask, onUpdateTask, editTask }) => {
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');

  const handleEditTask = task => {
    setTitle(task.title);
    setDate(task.date);
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (title.trim() !== '') {
      if (editTask) {
        // 編集モードの場合
        const updatedTask = { id: editTask.id, title, date };
        onUpdateTask(updatedTask);
      } else {
        // 追加モードの場合
        const newTask = { id: Date.now(), title, date };
        onAddTask(newTask);
      }
      setTitle('');
      setDate('');
    }
  };

  // 編集モードの場合はフォームの値をプリロード
  if (editTask) {
    handleEditTask(editTask);
  }

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="date"
        value={date}
        onChange={e => setDate(e.target.value)}
        placeholder="Enter a task"
      /><br />
      <input
        type="text"
        value={title}
        onChange={e => setTitle(e.target.value)}
        placeholder="Enter a task"
      /><br />
      <button type="submit">{editTask ? 'Update Task' : 'Add Task'}</button>
    </form>
  );
};

export default TaskForm;
