import React, { useState } from 'react';
import TaskForm from './TaskForm';

const TaskList = ({ tasks, onDeleteTask, onEditTask, onUpdateTask }) => {
  const [editTaskId, setEditTaskId] = useState(null);

  const handleEditTask = taskId => {
    setEditTaskId(taskId);
  };

  return (
    <ul style={styles.list}>
      {tasks
      .sort((a, b) => new Date(a.date) - new Date(b.date))
      .map(task => (
        <li key={task.id} style={styles.listItem}>
          <div style={styles.taskContent}>
            <p style={styles.taskDate}>{task.date}</p>
            <p style={styles.taskTitle}>{task.title}</p>
            <p style={styles.taskComment}>{task.comment}</p>
            <div>
              <button onClick={() => handleEditTask(task.id)} style={styles.editButton}>
                編集
              </button>
              <button onClick={() => onDeleteTask(task.id)} style={styles.deleteButton}>
                削除
              </button>
            </div>
          </div>
          {editTaskId === task.id && (
            <TaskForm
              onUpdateTask={onUpdateTask}
              editTask={task}
              isEditing={true}
              onFinishEditing={() => setEditTaskId(null)}
            />
          )}
        </li>
      ))}
    </ul>
  );
};

const styles = {
  list: {
    listStyleType: 'none',
    padding: '0',
  },
  listItem: {
    border: '1px solid #CCC',
    padding: '15px',
    marginBottom: '10px',
    borderRadius: '4px',
  },
  taskContent: {
    display: 'flex',
    flexDirection: 'column',
  },
  taskDate: {
    fontSize: '12px',
    margin: '0',
  },
  taskTitle: {
    fontSize: '20px',
    margin: '10px 0',
  },
  taskComment: {
    fontSize: '16px',
    margin: '10px 0 30px',
  },
  editButton: {
    marginRight: '10px',
    padding: '5px 10px',
    fontSize: '14px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  deleteButton: {
    padding: '5px 10px',
    fontSize: '14px',
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default TaskList;
