import React, { useState } from 'react';
import TaskForm from './TaskForm';

const TaskList = ({ tasks, onDeleteTask, onUpdateTask }) => {
  const [editTaskId, setEditTaskId] = useState(null);

  const handleEditTask = taskId => {
    setEditTaskId(taskId);
  };

  return (
    <ul style={styles.list}>
      {tasks.map(task => (
        <li key={task.id} style={styles.listItem}>
          <p>{task.title} {task.date}</p>
          <p>{task.comment}</p>
          <div>
            <button onClick={() => handleEditTask(task.id)} style={styles.button}>
              Edit
            </button>
            <button onClick={() => onDeleteTask(task.id)} style={styles.deleteButton}>
              Delete
            </button>
          </div>
          {editTaskId === task.id && (
            <TaskForm 
              onUpdateTask={onUpdateTask}
              editTask={task}
              onFinishEditing={handleFinishEditing}
            />
          )}
        </li>
      ))}
    </ul>
  );
};

const styles = {
  list: {
    listStyleType: 'none',
    padding: '0',
  },
  listItem: {
    border: '1px solid #CCC',
    padding: '15px',
    marginBottom: '10px',
    borderRadius: '4px',
  },
  button: {
    marginRight: '10px',
    padding: '5px 10px',
    fontSize: '14px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  deleteButton: {
    padding: '5px 10px',
    fontSize: '14px',
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default TaskList;
