import React, { useState } from 'react';
import TaskList from './TaskList';
import TaskForm from './TaskForm';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [editTask, setEditTask] = useState(null); // 追加: setEditTaskの定義

  const handleAddTask = newTask => {
    setTasks([...tasks, newTask]);
  };

  const handleDeleteTask = taskId => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  const handleEditTask = task => {
    setEditTask(task);
  };

  const handleUpdateTask = updatedTask => {
    setTasks(tasks.map(task => (task.id === updatedTask.id ? updatedTask : task)));
    setEditTask(null);
  };

  return (
    <div>
      <h1>Task Manager</h1>
      <TaskForm onAddTask={handleAddTask} onUpdateTask={handleUpdateTask} editTask={editTask} /> {/* 編集時のeditTaskを追加 */}
      <TaskList tasks={tasks} onDeleteTask={handleDeleteTask} onEditTask={handleEditTask} onUpdateTask={handleUpdateTask} />
    </div>
  );
};

export default App;
